CREATE TABLE `kaspi`.`tarifs` (`id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `role` ENUM('none','admin','parser','chrome_ext','site_user','premium_user') NOT NULL , `name` VARCHAR(256) NOT NULL , `price` DECIMAL(10,2) UNSIGNED NOT NULL DEFAULT '0' , `months` TINYINT UNSIGNED NOT NULL DEFAULT '0', `status` TINYINT(1) UNSIGNED NOT NULL DEFAULT '1' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
CREATE TABLE `kaspi`.`payments` (`id` INT UNSIGNED NOT NULL AUTO_INCREMENT , `tarifId` INT UNSIGNED NOT NULL , `userId` INT UNSIGNED NOT NULL , `createdAt` DATETIME NOT NULL , `status` ENUM('wait','paid','expired','canceled') NOT NULL DEFAULT 'wait' , `amount` DECIMAL(10,2) UNSIGNED NOT NULL DEFAULT '0' , `name` VARCHAR(256) NOT NULL DEFAULT '' , PRIMARY KEY (`id`)) ENGINE = InnoDB;
